import React, { useEffect } from "react";
import { useState } from "react";
import { Equipments } from '../components/Equipments';

function EquipmentList() {
    const [equipments, setEquipments] = useState([])
    const [allEquipments, setAllEquipments] = useState([])
    const [isSearched, setSearched] = useState(false)
    const URL = `http://127.0.0.1:8080/api/equipments`

    useEffect(() => {
        fetch(URL)
            .then(response => response.json())
            .then(data => setEquipments(data))
            .then(data => {
                const allEquipments = data.sort((a, b) => a.id.localeCompare(b.id));
                setEquipments(data)
                setAllEquipments(allEquipments)
            })
            .catch(err => console.log(err))
    }, [])

    // const EquipmentsElement = equipments.map((item, index) =>
    //     <Equipments key={index} item={item} />
    // )

    function searchChange(e) {
        const query = e.target.value.toLowerCase();
        if (query !== '') {
            setSearched(true);
            const searchedItems = allEquipments.filter(item => item.name.toLowerCase().includes(query));
