import React, { useEffect } from "react";
import { useState } from "react";
import { Equipments } from '../components/Equipments';

function EquipmentList() {
    const [equipments, setEquipments] = useState([])
    const [allEquipments, setAllEquipments] = useState([])
    const URL = `http://127.0.0.1:8080/api/equipments`

    useEffect(() => {
        fetch(URL)
            .then(response => response.json())
            .then(data => setEquipments(data))
            .then(data => {
                const allEquipments = data.sort((a, b) => a.id.localeCompare(b.id));
                setEquipments(data)
                setAllEquipments(allEquipments)
            })
            .catch(err => console.log(err))
    }, [])

    const EquipmentsElement = equipments.map((item, index) =>
        <Equipments key={index} item={item} />
    )
    const ItemLink = ({ item, isSearched }) => (
        <Link key={item.id} to={`/item/${item.name}`} className='items-card' >
            <img src={item.img} />
            {isSearched && <div>{item.name}</div>}
        </Link>
    )

    function searchChange(e) {
        const query = e.target.value.toLowerCase();
        if (query !== '') {
            setSearched(true);
            const searchedItems = allItems.filter(item => item.name.toLowerCase().includes(query));
            addItems(searchedItems);
            if (types !== false) {
                setTypes(false);
            }
        } else {
            setSearched(false);
            addItems(allItems);
        }
    }

    return (
        <div className='items-wrapper'>
            <div className="items-header">
                <div className="items-header-search">
                    <input type='text' placeholder="Search" onChange={searchChange} spellcheck='false' />
                </div>
            </div>
            <div className="items-content">
                {EquipmentsElement}
            </div>
        </div>
    )
}

export { EquipmentList }
