import React, { useEffect, useState } from "react";
import { Link } from 'react-router-dom';
import axios from '../api/axios'

function AdminUsers() {
    const [Users, setUsers] = useState([])
    const URL = `https://node-ror2.vercel.app/api/users`

    useEffect(() => {
        checkAdmin()
        getItems()
    }, [])

    function checkAdmin(){
        if(!localStorage.getItem('userInf') || JSON.parse(localStorage.getItem('userInf')).isAdmin == 0){
            window.location.href = "/";
        }
    }

    function getItems() {
        fetch(URL)
            .then(response => response.json())
            .then(data => {
                setUsers(data)
            })
            .catch(err => console.log(err))
    }

    function saveClick(){
        const itemFromDLC = document.getElementById('itemFromDLC').checked;
        const itemAbout = document.getElementById('itemAbout').innerText;
        const itemDescription = document.getElementById('itemDescription').innerText;
        const itemInf = ({name: itemName, about: itemAbout, rarity: itemRarity, category: itemCategory, stack: itemStack, description: itemDescription, FromDLC: itemFromDLC, img: itemImg, id: Item[0].id})
        updateItem(itemInf)
    }

    const ItemLink = ({ item, isSearched }) => (
        <Link key={item.id} to={`/item/${item.name}`} className='items-card' >
            <img src={item.img} />
            {isSearched && <div>{item.name}</div>}
        </Link>
    )

    return (
        <>
            <div className='admin-dashboard-wrapper'>

                <div className='admin-dashboard-menu'>
                <Link to="/admindashboard/characters">Characters</Link>
                <Link to="/admindashboard/items">Items</Link>
                <Link to="/admindashboard/equipments">Equipment</Link>
                <Link to="/admindashboard/users">Users</Link>
                </div>


                <div className='items-wrapper'>
                    <div className="items-content">

                    {Users.map(item => {
                            return (
                                <Link key={item.id} to={`/admin/user/${item.id}`} className='items-card'>
                                    <img src={item.img} />
                                    <p>{item.login}</p>
                                </Link>
                            )
                        })
                        }

                    </div>
                </div>
            </div>
        </>

    )
}

export { AdminUsers }
