import React, { useEffect } from "react";
import { useState } from "react";
import { Link } from 'react-router-dom';
import axios from '../api/axios'

import NotLikeIcon from '../icons/like-icon.svg'
import LikeIcon from '../icons/actove-like-icon.svg'


function ItemByName( {ItemName}){
    const [Item, setItem] = useState([])
    const [likeIcon, setLikeIcon] = useState(NotLikeIcon)
    const [favorite, setFavorite] = useState([])
    const URL = `http://127.0.0.1:8080/api/item/name/${ItemName}`

    function getFavorite(){
        try{
            var UserInf = JSON.parse(localStorage.getItem('userInf'))
            const URL = `http://127.0.0.1:8080/api/user/favorite/${UserInf.user_id}`
            fetch(URL)
            .then(response => response.json())
            .then(data => {
                let id = (data[0]['favorite_items'].split(','))
                setFavorite(id)
                console.log(12)
            })
          }catch (err){
            console.log(err)
          }
    }

    function LikeCheck(itemId){
        // try{
        //     var UserInf = JSON.parse(localStorage.getItem('userInf'))
        //     const URL = `http://127.0.0.1:8080/api/user/favorite/${UserInf.user_id}`
        //     fetch(URL)
        //     .then(response => response.json())
        //     .then(data => {
        //         let favorites_arr = data[0]['favorite_items'].split(',')
        //         favorites_arr
        //         .map(el =>{
        //             if(el == itemId){
        //                 setLikeIcon(LikeIcon)
        //             }
        //         })
        //     })
        //     .catch(err => console.log(err))
        //   }catch (err){
        //     console.log(err)
        //   }
        console.log(favorite)
          favorite.map(el =>{
                    if(el == itemId){
                        setLikeIcon(LikeIcon)
                    }
                })
    }

    function LikeClick(){
        let lol = getFavorite()
        console.log(lol)
    }

    useEffect(()=>{
        getFavorite()
        fetch(URL)
        .then(response => response.json())
        .then(data => {
            setItem(data)
            LikeCheck(data[0].id)
        })
        .catch(err => console.log(err))
    }, [])

    return(
        <div className='big-card-wrapper'>
                {Item.map((item, index) => (
                <div key={index} className='big-card'>

                    <div className="big-card-back">
                        <Link to={"/items"}>â†©Back</Link>
                        <button className="like-btn" onClick={LikeClick}><img src={likeIcon}/></button>
                    </div>

                    <div className="big-card-header">

                        <div className="big-card-header-img">
                            <img src={item.img}/>
                            <h1>{item.name}</h1>
                        </div>

                        <div className="big-card-header-stats">
                            <p>Rarity:  {item.rarity}</p>
                            <p>Category: {item.category}</p>
                            <p>Stack: {item.stack}</p>
                        </div>

                    </div>

                    <div className="big-card-text">
                        <p>{item.description}</p>
                    </div>

                </div>
                ))}
        </div>
    )
}

export { ItemByName }
