import React, { useEffect, useState, useRef, Component, useDeferredValue } from "react";
import { Link } from 'react-router-dom';
import { Signup } from '../components/Signup'

const USER_REGEX = /^[a-zA-Z0-9-_]{2,10}$/;
const PWD_REGEX =/^[a-zA-Z0-9-_]{4,20}$/;

const Register = () =>{
  const userRef = useRef();
  const errRef = useRef();

  const [user, setUser] = useState('');
  const [validName, setValidName] = useState(false);

  const [pwd, setPwd] = useState('');
  const [validPwd, setValidPwd] = useState(false);

  const [matchPwd, setMatchPwd] = useState('');
  const [validMatch, setValidMatch] = useState(false);
  const [matchFocus, setMatchFocus] = useState(false );

  const [errMs, setErrMsg] = useState('');
  const [success, setSucess] = useState(false);

  useEffect(() =>{
    userRef.current.focus(); 
  }, [])

  useEffect(() =>{
    const result = USER_REGEX.test(user)
    console.log(result)
    console.log(user)
    setValidName(result)
  }, [user])

  useEffect(() =>{
    const result = PWD_REGEX.test(pwd)
    console.log(result)
    console.log(pwd)
    setValidPwd(result)
    const match = pwd === matchPwd;
    setValidMatch(match)
  }, [pwd, matchPwd])

  useEffect(() =>{
    setErrMsg(' ')
  }, [user, pwd, matchPwd])

  return(
    <div className='register-form'>
               <form>
                    <h1>Register</h1>
                   <label className={validName ? "valid" : "form-label"}>Login: <input type="text" name="first_name" ref={userRef} onChange={(e) => setUser(e.target.value)} area-onvalid={validName ? "false" : "true"} onFocus={() => setUserFocus(true)} onBlur={() => setUserFocus(false)} required/> </label>
                   <label className={validPwd ? "valid" : "form-label"}>Password: <input type="password" name='password' ref={userRef} onChange={(e) => setPwd(e.target.value)} area-onvalid={validPwd ? "false" : "true"} required/> </label>
                   <label>Password confirm: <input type="password" name='password_confirm' /> </label>
                   <label>Email: <input type="email" name='email'/> </label>
                   <button type="submit">Submit</button>
                   <p>У вас уже есть аккаунт? <Link to="/">Авторизируйтесь</Link></p>
               </form>
    </div>
  )
}

