import React, { useEffect }from "react";
import { Link } from 'react-router-dom';
import axios from '../api/axios'

const NEWCHAR_URL = 'https://node-ror2.vercel.app/api/add/character'

function AdminNewCharacter(){
    useEffect(() => {
        checkAdmin()
    }, [])

    function checkAdmin(){
        if(!localStorage.getItem('userInf') || JSON.parse(localStorage.getItem('userInf')).isAdmin == 0){
            window.location.href = "/";
        }
    }

function saveClick() {
        const charImg = document.getElementById('itemImg').value;
        const charName = document.getElementById('charName').value;
        const itemRarity = document.getElementById('itemRarity').value;
        const itemCategory = document.getElementById('itemCategory').value;
        const itemStack = document.getElementById('itemStack').value;
        const itemFromDLC = document.getElementById('itemFromDLC').checked;
        const itemAbout = document.getElementById('itemAbout').innerText;
        const itemDescription = document.getElementById('itemDescription').innerText;
        const itemInf = ({name: itemName, about: itemAbout, rarity: itemRarity, category: itemCategory, stack: itemStack, description: itemDescription, FromDLC: itemFromDLC, img: itemImg})
        addItem(itemInf)
        }

    const addItem = async (itemInf) => {
        try{
            const response = await axios.post(NEWCHAR_URL, itemInf,
             {
              headers: {"Content-Type": 'application/json'}
             });
             window.location.href = "/admindashboard/items";
          } catch (err){
          }
}

    return(
        <div className='big-card-wrapper'>
                <div className='big-card'>

                    <div className="big-card-back">
                        <Link to={"/admindashboard/characters"}>â†©Back</Link>
                    </div>

                    <div className="big-card-header">

                        <div className="big-card-header-img">
                            <input type="text" placeholder="Character image URL" id="charIMG"/>
                            <h1><input type="text" placeholder="Name" id="charName"/></h1>
                        </div>

                        <div className="big-card-header-stats">
                            <p>Health: <input type="text" placeholder="Health" id="charHealth"/> </p>
                            <p>Health regen: <input type="text" placeholder="Health regen" id="charHealthReg"/> </p>
                            <p>Damage: <input type="text" placeholder="Damage" id="charDamage"/> </p>
                            <p>Speed: <input type="text" placeholder="Speed" id="charSpeed"/> </p>
                            <p>Armor: <input type="text" placeholder="Armor" id="charArmor"/> </p>
                        </div>

                    </div>

                    <div className="big-card-text">
                        <div className='itemChangeInfText' contenteditable="true" id="charDescription">Description</div>
                    </div>

                    <div className="big-card-text">
                        <div className='itemChangeInfText' contenteditable="true" id="charAbout"> About </div>
                    </div>

                    <div className="same-items-wrapper">
                        <button className="infChangeBtn">Save</button>
                    </div>

                </div>
        </div>
    )
}

export { AdminNewCharacter }
