import React, { useEffect, useState, useRef, Component } from "react";
import { Link } from 'react-router-dom';


function Register () {
  const [message, setMessage] = useState('');
  const firstNameRef = useRef(undefined);
  const lastNameRef = useRef(undefined);
  const loginRef = useRef(undefined);
  const passwordRef = useRef(undefined);


  const handleSubmit = (event) => {
    event.preventDefault();
    const first_name = firstNameRef.current.value;
    const last_name = lastNameRef.current.value;
    const login = loginRef.current.value;
    const password = passwordRef.current.value;
    setMessage(`Hello ${first_name} ${last_name} ${login} ${password}!`);
    event.target.reset();
  }
    return(

        <div className='register-form'>
            <form onSubmit={handleSubmit}>

                <label>Имя:<input type="text" id="first_name" name="first_name" ref={firstNameRef}/> </label>
                <label>Фамилия:<input type="text" id="last_name" name="last_name" ref={lastNameRef}/> </label>
                <label>Логин: <input type="text" id="login" name='login' ref={loginRef}/> </label>
                <label>Пароль: <input type="password" id="password"  name='password' ref={passwordRef}/> </label>
                <label>Подтвердите пароль: <input type="password" id="password_confirm" name='password_confirm'/> </label>
                <label>Почта: <input type="email" id="email" name='email'/> </label>

                <button type="submit">Submit</button>

             <h2>{message}</h2>
            </form>
