import React, { useEffect } from "react";
import { useState } from "react";
import { Link } from 'react-router-dom';
import axios from '../api/axios'

import NotLikeIcon from '../icons/like-icon.svg'
import LikeIcon from '../icons/actove-like-icon.svg'


function ItemByName( {ItemName}){
    const [Item, setItem] = useState([])
    const [favorite, setFavorite] = useState(NotLikeIcon)
    const URL = `http://127.0.0.1:8080/api/item/name/${ItemName}`

    function LikeCheck(){
        try{
            var UserInf = JSON.parse(localStorage.getItem('userInf'))
            const URL = `http://127.0.0.1:8080/api/user/favorite/${UserInf.user_id}`
            fetch(URL)
            .then(response => response.json())
            .then(data => {
                let favorites_arr = data[0]['favorite_items'].split(',')
                favorites_arr
                .map(el =>{
                    console.log(Item)
                    if(el == Item.id){
                        setFavorite(LikeIcon)
                    }
                })
            })
            .catch(err => console.log(err))
          }catch (err){
            console.log(err)
          }
    }

    function LikeClick(){
        if(favorite == LikeIcon){
            setFavorite(NotLikeIcon)
        } else if(favorite == NotLikeIcon){
            setFavorite(LikeIcon)
        }
    }

    useEffect(()=>{
        fetch(URL)
        .then(response => response.json())
        .then(data => setItem(data))
        .catch(err => console.log(err))
        LikeCheck(Item.id)
    }, [])

    return(
        <div className='big-card-wrapper'>
                {Item.map((item, index) => (
                <div key={index} className='big-card'>

                    <div className="big-card-back">
                        <Link to={"/items"}>â†©Back</Link>
                        <button className="like-btn" onClick={LikeClick}><img src={favorite}/></button>
                    </div>

                    <div className="big-card-header">

                        <div className="big-card-header-img">
                            <img src={item.img}/>
                            <h1>{item.name}</h1>
                        </div>

                        <div className="big-card-header-stats">
                            <p>Rarity:  {item.rarity}</p>
                            <p>Category: {item.category}</p>
                            <p>Stack: {item.stack}</p>
                        </div>

                    </div>

                    <div className="big-card-text">
                        <p>{item.description}</p>
                    </div>

                </div>
                ))}
        </div>
    )
}

export { ItemByName }
