import React, { useEffect, useState, useRef, Component} from "react";
import { Link, redirect } from 'react-router-dom';
import Cookies from 'js-cookie';
import axios from '../api/axios'

const AUTH_URL = 'http://localhost:8080/api/user/auth'

const Auth = () => {
    const userRef = useRef();

    const [user, setUser] = useState('');
    const [pwd, setPwd] = useState('');
    const [ErrMsg, setErrMsg] = useState('');
    const [success, setSucess] = useState(false);

    const [login, setValue] = useState('')
    const handleChange = (value) => {
    setValue(value)
  }

    const handleSubmit = async (e) =>{
       e.preventDefault()
        try{
            const authInf = ({login: user, password: pwd})
            const response = await axios.post(AUTH_URL, authInf,
                {
                 headers: {"Content-Type": 'application/json'}
                });
                 Cookies.set('login', response.data[0]['login'])
                 Cookies.set('email', response.data[0]['email'])
                 Cookies.set('userImg', response.data[0]['img'])
                 setSucess(true);
                 redirect("/profile");
        }catch (err){
      if(!err?.response){
        setErrMsg('No Server Response')
      } else if (err.response?.status == 409){
        setErrMsg('Wrong login or password')
      } else {
        setErrMsg('Registration Failed')
      }
    }
    }

    return(
            <div className='auth-form'>
                {success || Cookies.get('login') ? (
                <form>
                <p>Sing In success!</p>
                <p>{Cookies.get('login')}</p>
                <Link to="/">Go home</Link>
                </form>) : (
                <form onSubmit={handleSubmit}>
                    <h1>Sign In</h1>
                    <h1>{ErrMsg}</h1>
                    <label>Login: <input type="text" name="login" ref={userRef} onChange={(e) => setUser(e.target.value)}required/> </label>
                    <label>Password: <input type="password" name='password' ref={userRef} onChange={(e) => setPwd(e.target.value)} required/> </label>
                    <button>Sign In</button>
                    <p>Not registered? <Link to="/register">Sign Up</Link></p>
                </form>
                )}
            </div>
    )
}

export { Auth }
