import React, { useState, useEffect } from "react";

import SunIcon from '../icons/sun.png';
import MoonIcon from '../icons/moon.png';

const ThemeControl = () =>{
    const [themeIcon, setThemeIcon] = useState(SunIcon);

    useEffect(() => {
        // При загрузке компонента пытаемся восстановить ранее выбранную тему из localStorage
        const savedTheme = localStorage.getItem('selectedTheme');
        if(savedTheme){
        if (savedTheme === 'dark') {
            setDarkTheme();
        }else if(savedTheme === 'light'){
            setLightTheme();
        }}

    }, []);

    const setDarkTheme = () =>{
        document.querySelector('body').setAttribute('data-theme', 'dark');
        setThemeIcon(MoonIcon);
        // Сохраняем выбранную тему в localStorage
        localStorage.setItem('selectedTheme', 'dark');
    };

    const setLightTheme = () =>{
        document.querySelector('body').setAttribute('data-theme', 'light');
        setThemeIcon(SunIcon);
        // Сохраняем выбранную тему в localStorage
        localStorage.setItem('selectedTheme', 'light');
    };

    const toogleTheme = e =>{
        if(e.target.checked) setLightTheme();
        else setDarkTheme();
    };

    return(
        <div className="theme-control">
            <input type='checkbox' id='theme-input' onChange={toogleTheme} style={{display: 'none'}}/>
            <label htmlFor='theme-input'> <img src={themeIcon} alt='Theme Icon'/> </label>
        </div>
    );
};

export { ThemeControl };
